        fixUrl: function (url, preserveCase) {
            // drops http and www, lowercases and trims
            var u = url;
            if (u) {
                //console.log('***************** fixing url: ' + u);
                if (!preserveCase) {
                    u = u.toLowerCase();
                }
                u = u.trim();
                if (u.indexOf('://') >= 0) {
                    u = u.substring(u.indexOf('://') + 3);
                }
                if (u.indexOf('www.') === 0) {
                    u = u.substring(4);
                }
                if (u.lastIndexOf('/') === (u.length - 1)) {
                    u = u.substring(0, u.length - 1);
                }
                //console.log('***************** fixed url: ' + u);
            }
            return u;
        },

