                var url = store.url + '/services/data/v34.0/query/?q=SELECT+' + fieldsStr + '+FROM+' + recordType + whereStr;

                if (query && query.limit > 0)
                { url = url + '+LIMIT+' + query.limit; }
                else if (query && query.limit === 0)
                { }
